import React, { Component } from "react";
import EmployeeCard from "./components/EmployeeCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import employees from "./employees.json";
import Search from "./components/Search";

class App extends Component {
  // Setting this.state.friends to the employees json array
  state = {
    employees
  };

  employeeSearch = first => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const employees = this.state.employees.filter(employee => employee.name.first == first);
    // Set this.state.friends equal to the new friends array
    this.setState({ employees });
  };

   employeeSearch() {
    const [searchTerm, setSearchTerm] = React.useState("");
    const [searchResults, setSearchResults] = React.useState([]);
    const handleChange = e => {
      setSearchTerm(e.target.value);
    };
    React.useEffect(() => {
      const results = employees.filter(employee =>
        employee.toLowerCase().includes(searchTerm)
      );
      setSearchResults(results);
    }, [searchTerm]);
    this.setstate(results);
  }


  render() {
    return (
      <Wrapper>
        <Title>Employee List</Title>
        <Search></Search>
        {this.state.employees.map(employee => (
          <EmployeeCard 
            name={employee.name.first+" "+employee.name.last}
            image={employee.picture.large}
            email={employee.email}
            location={employee.location.country}
            serviceyears={employee.registered.age}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
