import React, { Component } from "react";
import API from "../utils/API";

class UserContainer extends Component {
    state = {
      result: {}
    };
    componentDidMount() {
        this.pullEmployees();
    }
    pullEmployees = query => {
        API.search(query)
        .then(res => this.setState({ result: res.data }))
        .catch(err => console.log(err));
    };
}

export default UserContainer;
